import {
  CAlert,
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCardText,
  CCardTitle,
  CCol,
  CContainer,
  CImg,
  CInput,
  CLabel,
  CLink,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CProgress,
  CRow,
  CSelect,
} from "@coreui/react";
import React, { Component, useState } from "react";
import $ from "jquery";
import swal from "sweetalert";
import CountdownTime from "../Practice/CountdownTime";
import Keyboard from "../keyboard";
const StopWatch = React.lazy(() => import("./StopWatch"));
const KeyboardSteno = React.lazy(() => import("../keyboard"));
const keySteno = ["S", "T", "K", "P", "R", "H"];
const keyQwerty = ["q", "a", "w", "s", "e", "d"];
const key = [
  {
    steno: "S",
    qwerty: "q",
  },
  {
    steno: "T",
    qwerty: "a",
  },
  {
    steno: "K",
    qwerty: "w",
  },
  {
    steno: "P",
    qwerty: "s",
  },
  {
    steno: "R",
    qwerty: "e",
  },
  {
    steno: "H",
    qwerty: "d",
  },
];
// function onchangeSelect() {
//   $("#vnInput").on("change", function () {
//     $("#stenoOutput").val($("#vnInput").val());
//     sweetAlert();
//   });
// }
const Progress = (props) => {
  return (
    <CProgress
      value={props.text}
      showValue
      className="mb-1 bg-grey"
      color={props.progress}
      style={{ width: "100%", margin: "auto" }}
    />
  );
};
function sweetAlert() {
  swal({
    title: "Thành công!",
    text: "Điểm của bạn: 50\nThời gian: 10s\n Tốc độ gõ: 10 từ/phút ",
    icon: "success",
  });
}

const Modal = (props) => {
  const [modal, setModal] = useState(false);

  const toggle = () => {
    setModal(!modal);
  };

  return (
    <CContainer>
      <CModal show={modal} onClose={toggle} size="xl">
        <CModalHeader closeButton>Đánh giá: Gõ từ sau bằng tốc ký</CModalHeader>
        <CModalBody style={{ textAlign: "center" }}>
          <CRow>
            <CCol sm="3">
              <CountdownTime time="10"></CountdownTime>
            </CCol>
            <CCol>
              <CAlert color="primary">P</CAlert>
            </CCol>
          </CRow>

          <CCol>
            {/* <CInput></CInput>
             */}
            <Keyboard></Keyboard>
          </CCol>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary" id="add" onClick={(toggle, sweetAlert)}>
            Nộp bài
          </CButton>{" "}
          {/* <CButton color="secondary" onClick={toggle}>
            Hủy
          </CButton> */}
        </CModalFooter>
      </CModal>
    </CContainer>
  );
};
class LessonLeys2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 0,
      index: 0,
      finish: false,
    };
  }

  toggle = () => {
    this.setState((state) => ({
      finish: !state.finish,
    }));
    this.refresh()
  };

  refresh = () => {
    this.setState((state) => ({
      value: 0,
    }));
    this.setState((state) => ({
      index: 0,
    }));
  }

  onChangeParent = () => {
    console.log("parent")
    let index = this.state.index;
    index += 1;
    this.setState({ index });
    let value = this.state.value;
    value += 100/6;
    this.setState({ value });
    console.log(value);

    if (index == 6) {
      this.toggle();
    }
  };

  render() {
    return (
      <>
        <CModal show={this.state.finish}>
          <CModalHeader>Đánh giá bài học của bạn</CModalHeader>
          <CModalBody>
            <CRow>
              <CCol sm="8">
                <h6> Độ chính xác: </h6>
                <h6> Thời gian: </h6>
                <h6> Tốc độ: </h6>
              </CCol>
              <CCol sm="4">
                <h6>94%</h6>
                <h6>0:20s</h6>
                <h6>45 từ/phút</h6>
              </CCol>
            </CRow>
          </CModalBody>
          <CModalFooter>
            <CLink to="/learn/keys">
              <CButton color="primary">Bài giảng tiếp theo</CButton>{" "}
            </CLink>
            <CButton color="secondary" onClick={this.toggle}>
              Học lại
            </CButton>
          </CModalFooter>
